#include <unistd.h>
#include <iostream>
#include <string>
using namespace std;

class Solution {
public:
    string shortestPalindrome(string s) {
        int len = s.size();
        if (len < 2)
            return s;

        int pos = 1;
        for (int curr_pos = 0; curr_pos <= (len >> 1); ) {
            int p1 = curr_pos;
            int p2 = curr_pos;

            for (; p2 < len - 1 && s[p2 + 1] == s[p2]; p2++)
                ;
            curr_pos = p2 + 1;

            for (; p1 > 0 && p2 < len - 1; p1--, p2++)
                if (s[p1 - 1] != s[p2 + 1])
                    break;

            if (p1 == 0 && p2 < len)
                pos = max(pos, p2 + 1);
        }

        string tmp = s.substr(pos);
        reverse(tmp.begin(), tmp.end());

        return tmp + s;
    }
};
























int main()
{
    Solution sol;
    string s = "";
    while (cin >> s)
        cout << sol.shortestPalindrome(s) << endl;

    return 0;
}
